SELECT COUNT(*)
FROM member





		AND enabled = #{enabled}

SELECT m.userCode, m.email, userName, userType, nickName, m.regDate, lastLogin, gender, enabled, TO_CHAR(birth, 'YYYY-MM-DD') birth FROM member m
AND enabled = #{enabled}
ORDER BY regDate DESC OFFSET #{offset} ROWS FETCH FIRST #{size} ROWS ONLY
