mapper.select:
SELECT 'community' tableName, c.communityNum, c.subject, c.regDate FROM community c JOIN member m ON m.userCode = c.userCode WHERE c.userCode = #{userCode}
UNION ALL
SELECT 'recruit' tableName, r.recruitNum, r.subject, r.regDate FROM recruit r JOIN member m ON m.userCode = r.userCode WHERE r.userCode = #{userCode}
ORDER BY regDate DESC OFFSET #{offset} ROWS FETCH FIRST #{size} ROWS ONLY

SELECT NVL(COUNT(*), 0) FROM ( SELECT 'community' tableName, c.communityNum, c.subject, c.regDate FROM community c JOIN member m ON m.userCode = c.userCode WHERE c.userCode = #{userCode}
UNION ALL
SELECT 'recruit' tableName, r.recruitNum, r.subject, r.regDate FROM recruit r JOIN member m ON m.userCode = r.userCode WHERE r.userCode = #{userCode} )
